import Layout from './layout'
import tps from '../utils/blog_types'
// import banner from '../images/banner'
import Event from '../utils/emiter';
var timer = null;
export default class extends React.Component {
  static async getInitialProps({ req,query,jsonPageRes }) {
    const userAgent = req ? req.headers['user-agent'] : navigator.userAgent
    return { userAgent,query,jsonPageRes }
  }

  state = {
      path:"",
      headerTop:0
  }

  async componentDidMount(){
      document.onscroll = () => {
          var scrollTop = document.documentElement.scrollTop || window.pageYOffset || document.body.scrollTop;
          if (scrollTop > 62 + 20){
              this.setState({headerTop:-61})
          }else{
              this.setState({headerTop:0})
          }          
      }
      //document.getElementById("t").setAttribute("class","shadownone")
      //}, 1000);


      // let rightHeight = document.getElementsByClassName("right_box")[0].clientHeight;
      // if (scrollTop > 62 + 20){

      // }
      this.setState({path:location.pathname})
  }

  render() {
    const {path,headerTop} = this.state
    return (
      <div>
        <div style={{width:"100%",height:61,transition:"0.3s",WebkitTransition:"0.3s",backgroundColor:"rgba(255,255,255,0.9)",position:"fixed",borderBottom:"1px solid #ddd",boxShadow:"0 1px 1px rgba(0,0,0,.04)",zIndex:999,top:headerTop,left:0}}>
          <div style={{width:"100%",margin:"0 auto",height:"100%",lineHeight:"60px",fontSize:22}}>
            <div style={{width:"100%",textAlign:"center"}}>Simple</div>
            <h2 id="mnavh" class="">
              <span className="navicon"></span>
              <span className="navicon"></span>
              <span className="navicon"></span>
            </h2>        
            {/* <ul id="starlist" style={{float:"right",margin:0}}>
              <li><a href="/" style={path == "/" ? {color:"#f65a8a"}:{}}>首页</a></li>
              <li><a href="/article" style={path == "/article" || path == "/detail" ? {color:"#f65a8a"}:{}}>文章</a></li> 
              <li><a href="/message" style={path == "/message" ? {color:"#f65a8a"}:{}}>留言</a></li> 
              <li><a href="/me" style={path == "/me" ? {color:"#f65a8a"}:{}}>关于</a></li> 
            </ul> */}
          </div> 
        </div>
        <div style={{height:75}}></div>
        <style jsx>{`
          #mnavh{
            position: absolute;
            display: block;
            top: 8px;
            left: 10px;
          }
          .navicon {
            display: block;
            position: relative;
            width: 30px;
            height: 5px;
            background-color: #000;
            margin-top: 20px;
          }
          #starlist li {
              float: left;
              display: block;
              padding: 0 0 0 40px;
              font-size: 16px;
          }
          #starlist li a{
            text-decoration: none;
            color: #555;
          }
        `}</style>
      </div>  
    )
  }
}
